import React, { useEffect, useState } from 'react';

function Lesson() {
  const [puzzle, setPuzzle] = useState(null);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState('');
  const [answer, setAnswer] = useState('');
  const [feedback, setFeedback] = useState('');
  const [correctAnswers, setCorrectAnswers] = useState([]);

  useEffect(() => {
    const username = localStorage.getItem('username');
    const fetchPuzzle = async () => {
      try {
        const res = await fetch(`http://localhost:5000/members?username=${username}`);
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        const data = await res.json();
        setMessage(data.message);
        setPuzzle(data.puzzle);
        setCorrectAnswers(data.answers || []);
      } catch (err) {
        setError(err.message);
      }
    };
    fetchPuzzle();
  }, []);

  const checkAnswer = () => {
    const normalized = answer.trim().toLowerCase();
    const isCorrect = correctAnswers.some(ans => ans.trim().toLowerCase() === normalized);
    setFeedback(isCorrect ? '✅ Correct!' : '❌ Try again.');
  };

  if (error) return <div>Error loading puzzle: {error}</div>;
  if (!puzzle) return <div>Loading puzzle...</div>;

  return (
    <div>
      <h2>{message}</h2>
      <p>{puzzle}</p>

      <input
        type="text"
        placeholder="Your answer"
        value={answer}
        onChange={(e) => setAnswer(e.target.value)}
      />
      <button onClick={checkAnswer}>Submit Answer</button>

      {feedback && <p>{feedback}</p>}
    </div>
  );
}

export default Lesson;
