import React, { useEffect, useState } from 'react';

function Lesson() {
  const [puzzle, setPuzzle] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPuzzle = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/puzzles?level=1');
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        const data = await res.json();
        setPuzzle(data);
      } catch (err) {
        setError(err.message); // <-- catch errors
      }
    };
    fetchPuzzle();
  }, []);
  
  if (error) return <div>Error loading puzzle: {error}</div>;
  if (!puzzle) return <div>Loading puzzle...</div>;

  return (
    <div>
      <h2>{puzzle.title}</h2>
      <p>{puzzle.description}</p>
      {/* Later we'll add clues and questions here */}
    </div>
  );
}

export default Lesson;
