import React from 'react';

export function AuroraBackground({ children }) {
  return (
    <div className="relative w-full h-screen overflow-hidden bg-gradient-to-r from-purple-500 via-pink-500 to-red-500">
      {/* Animated blobs */}
      <div className="absolute top-0 left-0 w-96 h-96 bg-purple-400 opacity-30 rounded-full filter blur-3xl animate-pulse"></div>
      <div className="absolute bottom-0 right-0 w-96 h-96 bg-pink-400 opacity-30 rounded-full filter blur-3xl animate-pulse"></div>

      {/* Your Content */}
      {children}
    </div>
  );
}
